RSpec.describe "/banners", type: :request do
  let(:admin) { create(:user, :admin) }

  let(:valid_attributes) do
    {
      content: "Welcome to AWBW!",
      show: true,
      created_by_id: admin.id,
      updated_by_id: admin.id
    }
  end

  let(:invalid_attributes) do
    { content: "", show: nil, created_by_id: nil, updated_by_id: nil }
  end

  before { sign_in admin } # Devise helper for authenticated routes

  describe "GET /index" do
    it "renders a successful response" do
      Banner.create! valid_attributes
      get banners_url
      expect(response).to be_successful
    end
  end

  describe "GET /show" do
    it "renders a successful response" do
      banner = Banner.create! valid_attributes
      get banner_url(banner)
      expect(response).to be_successful
    end
  end

  describe "GET /new" do
    it "renders a successful response" do
      get new_banner_url
      expect(response).to be_successful
    end
  end

  describe "GET /edit" do
    it "renders a successful response" do
      banner = Banner.create! valid_attributes
      get edit_banner_url(banner)
      expect(response).to be_successful
    end
  end

  describe "POST /create" do
    context "with valid parameters" do
      it "creates a new Banner" do
        expect {
          post banners_url, params: { banner: valid_attributes }
        }.to change(Banner, :count).by(1)
      end

      it "redirects to the banners index" do
        post banners_url, params: { banner: valid_attributes }
        expect(response).to redirect_to(banners_url)
      end
    end

    context "with invalid parameters" do
      it "does not create a new Banner" do
        expect {
          post banners_url, params: { banner: invalid_attributes }
        }.to change(Banner, :count).by(0)
      end

      it "renders a response with 422 status (i.e. to display the 'new' template)" do
        post banners_url, params: { banner: invalid_attributes }
        expect(response).to have_http_status(:unprocessable_content)
      end
    end
  end

  describe "PATCH /update" do
    context "with valid parameters" do
      let(:new_attributes) do
        valid_attributes.merge(
          content: "Updated welcome message for AWBW"
        )
      end

      it "updates the requested banner" do
        banner = Banner.create! valid_attributes
        patch banner_url(banner), params: { banner: new_attributes }
        banner.reload
        skip("Add assertions for updated state")
      end

      it "redirects to the banners index" do
        banner = Banner.create! valid_attributes
        patch banner_url(banner), params: { banner: new_attributes }
        banner.reload
        expect(response).to redirect_to(banners_url)
      end
    end

    context "with invalid parameters" do
      it "renders a response with 422 status (i.e. to display the 'edit' template)" do
        banner = Banner.create! valid_attributes
        patch banner_url(banner), params: { banner: invalid_attributes }
        expect(response).to have_http_status(:unprocessable_content)
      end
    end
  end

  describe "DELETE /destroy" do
    it "destroys the requested banner" do
      banner = Banner.create! valid_attributes
      expect {
        delete banner_url(banner)
      }.to change(Banner, :count).by(-1)
    end

    it "redirects to the banners list" do
      banner = Banner.create! valid_attributes
      delete banner_url(banner)
      expect(response).to redirect_to(banners_url)
    end
  end
end
